mapper.select:
select  t_cpz_product_level.productCategoryName, t_cpz_product_level.parentId, t_cpz_product_level.parentName, t_cpz_product_level.productCategoryGrade, t_cpz_product_level.path, t_cpz_plat_product.productID, t_cpz_plat_product.productName, t_cpz_plat_product.productCategoryId, t_cpz_plat_product.productType, t_cpz_plat_product.productStatus, t_cpz_plat_product.tradingUnit, t_cpz_plat_product.productColor, t_cpz_plat_product.isFresh, t_cpz_plat_product.sourceArea, t_cpz_plat_product.isCanRefund, t_cpz_plat_product.isNeedSpot, t_cpz_plat_product.productDetail, t_cpz_plat_product.supportDay, t_cpz_plat_product.channelNo, t_cpz_plat_product_pic.picName, t_cpz_plat_product_pic.picShowNo, t_cpz_plat_product_pic.picNorms, t_cpz_plat_product_pic.picUrl, t_cpz_plat_product_norms.normsId, t_cpz_plat_product_norms.normsName from t_cpz_plat_product inner join t_cpz_product_level on t_cpz_product_level.productCategoryId=t_cpz_plat_product.productCategoryId inner join t_cpz_plat_product_pic on t_cpz_plat_product_pic.productId=t_cpz_plat_product.productID inner join t_cpz_plat_product_norms on t_cpz_plat_product_norms.productId=t_cpz_plat_product.productID where 		
 t_cpz_plat_product.productName= #{productName}


 and t_cpz_plat_product.productType= #{productType}
